var _ = require( 'lodash' );
var expect = require( 'expect' );
var React = require( 'react' );
var Component = require( '../' );

var fixtures = require( '../fixture' );

describe( Component.id + ' #component', function () {
  _( fixtures ).each( function ( fixture ) {
    it( fixture.name + ' should render as expected', function () {
      var component = Component( fixture.fixture );
      var render = React.renderComponentToStaticMarkup( component );
      expect( render ).toEqual( fixture.render );
    } );
  } );
} );